public class ArrayCountsToZero {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int[] array;
	array = new int [11];

		System.out.println(" Index \t  Value");
	
		for(int i = 0; i < array.length; i++)
			System.out.printf("%5d%8d%n", i, array[i]);	
	

	}

}
