package exercise210;

public class Xy {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 2;
		int y = 3;
		
		System.out.printf("x = %d%n", x);
		System.out.printf("Value of %d + %d is %d%n", x, x, (x + x));
		System.out.printf("x =");
		System.out.printf("%n%d = %d%n", (x + y), (y + x));
	}

}
